"use strict";(self.webpackChunkframework=self.webpackChunkframework||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"playbookSidebar":[{"type":"link","label":"Home","href":"/","docId":"home","unlisted":false},{"type":"category","label":"Strategize","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Vision, mission, and impact","href":"/strategize/mission/","docId":"strategize/mission/vision_mission_impact","unlisted":false},{"type":"link","label":"Portfolio management","href":"/strategize/portfolio/","docId":"strategize/portfolio/portfolio_management","unlisted":false}],"href":"/strategize/"},{"type":"link","label":"Organize","href":"/organize/","docId":"organize/organize","unlisted":false},{"type":"category","label":"Design","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Ethics and security","href":"/design/ethics_security/","docId":"design/ethics_security/ethics_security","unlisted":false}],"href":"/design/"},{"type":"link","label":"Build","href":"/build/","docId":"build/build","unlisted":false}]},"docs":{"build/build":{"id":"build/build","title":"Build","description":"Our building process involves creating robust infrastructure, establishing clear policies, and managing workflows and releases effectively. We ensure every asset and product version is crafted with precision, guided by continuous performance reviews and feedback, all in service of our mission.","sidebar":"playbookSidebar"},"design/design":{"id":"design/design","title":"Design","description":"Design at RepublicOfData.io is a confluence of aesthetics, functionality, and ethics. Our domain designs and asset architectures are not just blueprints of technology but of responsible innovation. Ethics and security are not afterthoughts but integral to our design philosophy.","sidebar":"playbookSidebar"},"design/ethics_security/ethics_security":{"id":"design/ethics_security/ethics_security","title":"Ethics and security","description":"At RepublicofData.io, our commitment to compliance with data protection regulations is not just a legal obligation -- it\'s a core element in our ethical approach to data management. This section of the Playbook outlines our practices and principles to not only ensure GDPR and CCPA compliance, but to exceed the minimum requirements of these regulations where necessary and good for society.","sidebar":"playbookSidebar"},"home":{"id":"home","title":"Home","description":"RepublicOfData.io is more than just a data product building agency; we\'re architects of social change. Our expertise lies in empowering organizations to realize their social missions through innovative, ethical, and impactful data solutions.","sidebar":"playbookSidebar"},"organize/organize":{"id":"organize/organize","title":"Organize","description":"Organization in our context is about defining roles and responsibilities, optimizing workflows, and ensuring quality at every stage. Each team member understands their role in our mission, and is empowered by a clear QA process that upholds our standards of excellence.","sidebar":"playbookSidebar"},"strategize/mission/vision_mission_impact":{"id":"strategize/mission/vision_mission_impact","title":"Vision, mission, and impact","description":"At RepublicofData.io, we start every 6-week sprint with product discovery, which is then quickly followed by prototyping, user feedback, product iteration, and finally a working Minimum Valuable Product.","sidebar":"playbookSidebar"},"strategize/portfolio/portfolio_management":{"id":"strategize/portfolio/portfolio_management","title":"Portfolio management","description":"The EthicalOS Risk Mitigation Checklist was developed by the Institute for the Future (IFTF) as part of the broader Ethical OS Toolkit. The toolkit was created to help organizations anticipate and navigate the ethical implications of technology. While not as widely known as some other frameworks, it has been utilized by organizations committed to responsible innovation.","sidebar":"playbookSidebar"},"strategize/strategize":{"id":"strategize/strategize","title":"Strategize","description":"Our strategy begins with defining vision, mission, and impact. We then meticulously define the purpose of each data product, ensuring alignment with overarching goals. Portfolios are managed and roadmaps are crafted not just for product success, but for societal advancement.","sidebar":"playbookSidebar"}}}')}}]);